<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Example extends MLMS_Controller {   function __construct()   {        parent::__construct();        $this->load->model('admin/settings_model');        $configarr = $this->settings_model->getItems();         date_default_timezone_set($configarr[0]['time_zone']);   }   function index()    {        $fb_config = array(            'appId'  => '',            'secret' => ''        );        $this->load->library('facebook', $fb_config);        $user = $this->facebook->getUser();        if ($user) {            try {                $data['user_profile'] = $this->facebook                    ->api('/me');            } catch (FacebookApiException $e) {                $user = null;            }        }        if ($user) {            $data['logout_url'] = $this->facebook                ->getLogoutUrl();        } else {            $data['login_url'] = $this->facebook                ->getLoginUrl();        }        $this->load->view('view',$data);    }  }?>