<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Teacher extends MLMS_Controller {	function __construct()	{		parent::__construct();		$this->template->set_layout('frontend');        $this->load->helper('text');        $this->load->model('Teacher_model');        $this->load->model('admin/settings_model');$configarr = $this->settings_model->getItems(); date_default_timezone_set($configarr[0]['time_zone']);	}	public function index($parent_id = NULL)	{       $tmpl = "default";       $this->template->set("tmpl", $tmpl);      // print_r($this->Teacher_model->getTeacherList());       $this->template->set("teachers", $this->Teacher_model->getTeacherList());	   $this->template->build('teacher/list');	}   public function view($tid = NULL)	{       $tmpl = "default";       $tid = ( $this->uri->segment(3) )  ? $this->uri->segment(3) : NULL;       if(!isset($tid) && $tid == ''){          redirect('teacher');	    }       $this->load->model('Program_model');	   //$cat_id = ($parent_id != 0) ? filter_var($cat_id, FILTER_VALIDATE_INT) : NULL;       $this->template->set("tmpl", $tmpl);       //$this->template->set("teachers", $this->Teacher_model->getTeacherList());       $teacher_info = $this->Program_model->getTeacherInfo($tid);       $this->template->set("teacher_info", $teacher_info);       $teachercourse = $this->Program_model->getTeacherCourse($tid);      // print_r($teachercourse);       $this->template->set("teacher_course", $teachercourse);	   $this->template->build('teacher/view');	}    }    ?>