<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class Welcome extends CI_Controller{	function __construct()	{		parent::__construct();		$this->load->helper('url');		$this->load->library('tank_auth');		$this->load->model('admin/settings_model');$configarr = $this->settings_model->getItems();	date_default_timezone_set($configarr[0]['time_zone']);	}	function index()	{		if (!$this->tank_auth->is_logged_in()) {			redirect('/auth/login/');		} else {			$data['user_id']	= $this->tank_auth->get_user_id();			$data['username']	= $this->tank_auth->get_username();			$data['fullname']	= $this->session->userdata('firstname') . " " . $this->session->userdata('lastname');			$this->load->view('welcome', $data);		}	}}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */