<div id="main" role="main"><div class="holder" id="mrp-container2"><div id="system-message-container"></div><div class="title">Order Detail</div><div class="content"><div id="system-message-container"></div><input type="button" class="button" value="Print" onclick="javascript:window.print(); return false;"> <?php$order = $get_orders[0];$promocodeid = $order->promocodeid;$discount_details = array();$currency = $order->currency;//$character = "Currency".$currency;$currencypos = $get_config[0]['currencypos'];//$configs = $this->Buyitem_model->getConfigs();	//$currency = $configs["0"]["currency"];   //	$currencypos = $configs["0"]["currencypos"];$character = "CURRENCY_".$currency;	switch($character){		case 'CURRENCY_INR' :		$currencyChar = "Rs.";		case 'CURRENCY_MXN' :		$currencyChar = "$";		break;		case 'CURRENCY_USD' :		$currencyChar = "$";		break;		case 'CURRENCY_AUD' :		$currencyChar = "$";		break;		case 'CURRENCY_CAD' :		$currencyChar = "$";		break;	}if($currencypos == 0){	$discount =$currencyChar." 0.00";}else{	$discount = "0.00 ".$currencyChar;}if($promocodeid != "0"){    $discount_details = $this->Myinfo_model->getDiscountDetails($promocodeid);    	if($discount_details[0]->typediscount == "0"){    		if($currencypos == 0){    			$discount = $currencyChar." ".$discount_details[0]->discount;    		}    		else{    			$discount = $discount_details[0]->discount." ".$currencyChar;    		}    	}    	else{    		$discount = $discount_details[0]->discount."%";    	}}?><form action="index.php" name="adminForm" method="post">xyz	<div id="contentpane">		<table width="100%" cellspacing="0" cellpadding="3" bordercolor="#cccccc" border="1" class="adminlist" style="border-collapse: collapse;">			<caption class="componentheading"><?php echo "Order No  " ?><?php echo $order->id ?>: <?php echo $order->status; ?></caption>		</table>	<span align="left"><b>Date	<?php echo date('d-m-Y',strtotime($order->order_date)); ?></b></span>	<br><br>    <span style="font-weight:bold;">Billed To</span>	<br>		<table class="guru_customer_details">			<tbody><tr>				<td>First name:</td>				<td style="padding-left:20px;"><?php echo $user_info['first_name'];?></td>			</tr>			<tr>				<td>Last name:</td>				<td style="padding-left:20px;"><?php echo $user_info['last_name']; ?></td>			</tr>            <!--<tr>				<td>Company:</td>				<td style="padding-left:20px;"></td>			</tr>-->		</tbody></table>	<table class="order_table">		<thead>			<tr>				<th></th>				<th>Course Name</th>				<th>Quantity</th>				<th>Price</th>				<th>Discount amount</th>				<th>Total</th>			</tr>		</thead>		<tbody>         <?php		$ids = "0";		$id_price = array();		$id = array();		if(trim($order->courses) != ""){			$temp1 = explode("|", trim($order->courses));			if(is_array($temp1) && count($temp1) > 0){				foreach($temp1 as $key=>$value){					$temp2 = explode("-", $value);                    //print_r($temp2);					$id[] = trim($temp2["0"]);				    $id_price[trim($temp2["0"])]["price"] = trim($temp2["1"]);					$id_price[trim($temp2["0"])]["quantity"] = trim($temp2["2"]);				}			}		}       	$courses = "";		if(isset($id) && count($id) > 0){			//$courses = guruModelguruOrder::getCourses(implode(",", $id));           $courses = $this->Myinfo_model->getOrderByid(implode(",", $id));           //$this->Myinfo_model->getCustomerDetails($order->userid);		}		if(isset($courses) && is_array($courses) && count($courses) > 0){			$i = 0;			$k = 1;            //print_r($courses);			foreach($courses as $key=>$value){ ?>                <tr class="<?php echo "row".$i; ?>">    				<td><?php echo $k; ?></td>    				<td><?php echo $value->name; ?></td>    				<td>1</td>    				<td>                    <?php if($currencypos == 0){    					echo $currencyChar." ".$id_price[$value->id]["price"];    				}    				else{    					echo $id_price[$value->id]["price"]." ".$currencyChar;    				}?></td>    				<td><?php echo $discount; ?></td>    				<td><?php    				if($currencypos == 0){    					 echo $currencyChar." ".round(((float)$id_price[$value->id]["price"]), 2);    				}    				else{    					 echo round(((float)$id_price[$value->id]["price"]), 2)." ".$currencyChar;    				}    				 ?></td>    			</tr>            <?php }   ?>            <tr>				<td colspan="4"></td>				<td style="font-weight: bold; text-align:right;"><?php echo "Sub Total" ?></td>				<td><?php						if($currencypos == 0){							echo $currencyChar." ".$order->amount;						}						else{							echo $order->amount." ".$currencyChar;						}				 ?></td>			</tr>            <tr>				<td colspan="4"></td>				<td style="font-weight: bold; text-align:right;"><?php echo "Discount amount"; ?></td>				<td><?php  echo $discount; ?></td>			</tr>            <tr>				<td colspan="4"></td>					<td style="font-weight: bold; text-align:right;">Total</td>				<td>					<?php					if(is_array($discount_details) && count($discount_details) > 0){						if($discount_details[0]->typediscount == '0') {//use absolute value							$difference = $order->amount - (float)$discount_details[0]->discount;							echo $orderamount = $difference;						}						else{                        $orderamount = $order->amount * (float)$discount_details[0]->discount / 100;                        $orderamount *= 1 - (float)$discount_details[0]->discount / 100;						}					}					if(isset($order->amount_paid) && trim($order->amount_paid) != ""){						if($currencypos == 0){							echo $currencyChar." ".$order->amount_paid;						}						else{							echo $order->amount_paid." ".$currencyChar;						}					}					else{						if($currencypos == 0){							echo $currencyChar." ".$order->amount;						}						else{							echo $orderamount." ".$currencyChar;						}					}					?>				</td>			</tr>           <?php }?>		</tbody>	</table>	</div></form><div id="rich-text1"><div class="weblet-inner"><div class="steps-holder"></div></div></div><div id="rich-text2"><div class="weblet-inner"></div></div><div id="rich-text3"><div class="weblet-inner"><!--<div class="search-block"></div> --></div></div><div id=