<div id="main" role="main"><div class="holder" id="mrp-container2"><div id="system-message-container"></div><?php if ($teachers): ?><h1>Teachers:</h1><div class="course_row_guru"><?php foreach($teachers as $teacher){//  print_r($teacher); ?><div style="width:50%" class="course_cell_guru"><table width="100%" cellspacing="0" cellpadding="0"><tbody><tr><td width="5%" style="vertical-align:top;"><div class="image_guru"><?php if($teacher->images){?><a href='<?php echo base_url(); ?>/category/view/<?php echo $teacher->user_id; ?>'><img src="<?php echo base_url(); ?>/public/uploads/users/img/<?php echo $teacher->images; ?>" class="image_guru"></a><?php }else{ ?> <img class="image_guru" src="<?php echo base_url(); ?>public/default/images/no_images.jpg" width="120px" height="120px"><?php } ?></div></td><td style="vertical-align:top;"><div class="name_guru"><a href='<?php echo base_url(); ?>teacher/view/<?php echo $teacher->user_id; ?>'><?php echo $teacher->first_name; ?></a></div><div style="text-align:left;" class="description_guru"><?phpecho character_limiter($teacher->full_bio, 400);?></div><div style="text-align:right" class="readon"><a href="<?php echo base_url(); ?>teacher/view/<?php echo $teacher->id; ?>">Read more</a></div></td></tr></tbody></table></div><?php } ?> </div><?php else: ?><p class='text'><?php echo lang('web_no_elements');?></p><?php endif ?> </div> </div>